#include <string>
#include <iostream>
#include <stack>
#include <unordered_map>

using namespace std;

class Solution {
public:
    bool isValid(string s) {
        int n = s.size();
        if (n % 2 == 1) {
            return false;
        }

        unordered_map<char, char> pairs = {
                {')', '('},
                {']', '['},
                {'}', '{'}
        };
        stack<char> stk;
        for (char ch: s) {  // 遍历字符串
            if (pairs.count(ch)) {  // 如果配对右括号
                if (stk.empty() || stk.top() != pairs[ch]) {    // 栈空、或者与栈顶数据不匹配
                    return false;
                }
                stk.pop();
            }
            else {
                stk.push(ch);   // 压入左半部分
            }
        }
        return stk.empty(); // 判断是否全部清空
    }
};

class Solution2 {
public:
    bool isValid(string s) {
        string stack="";
        for(int i=0; i<s.size(); i++){
            if(s[i]=='(' or s[i]=='[' or s[i]=='{')
                stack.push_back(s[i]);
            else if(s[i]==')'){
                if(stack.size()==0)
                    return false;
                char temp=stack[stack.length()-1];
                stack.pop_back();
                if(temp!='(')
                    return false;
            }
            else if(s[i]==']'){
                if(stack.size()==0)
                    return false;
                char temp=stack[stack.length()-1];
                stack.pop_back();
                if(temp!='[')
                    return false;
            }
            else if(s[i]=='}'){
                if(stack.size()==0)
                    return false;
                char temp=stack[stack.length()-1];
                stack.pop_back();
                if(temp!='{')
                    return false;
            }
        }
        if(stack.size()==0)
            return true;
        return false;
    }
};

